import React from "react";
import { Link } from 'react-router-dom';
import { pluralize } from '../../utils/helpers';
import { useDispatch, useSelector } from "react-redux";
import { ADD_TO_CART, UPDATE_CART_QUANTITY } from "../../utils/actions";
import { idbPromise} from "../../utils/helpers";
// import Love from '../assests/paintings/reds/love.jpg';

function ProductItem(item) {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    const {
        image,
        name,
        _id,
        price,
        quantity
    } = item;

    const { cart } = state;

    const addToCart = () => {
        const itemInCart = cart.find((cartItem) => cartItem._id === _id)
        if (itemInCart) {
            dispatch({
                type: UPDATE_CART_QUANTITY,
                _id: _id,
                purchaseQuantity: parseInt(itemInCart.purchaseQuanity) + 1
            });
            idbPromise('cart', 'put', {
                ...itemInCart,
                purchaseQuantity: parseInt(itemInCart.purchaseQuantity) + 1
            });
        } else {
            dispatch({
                type: ADD_TO_CART,
                product: {...item, purchaseQuantity: 1}
            });
            idbPromise('cart', 'put', {...item, purchaseQuantity: 1 });
        }
    }

    // key={product._id}
    //   _id={product._id}
    //   image={product.image}
    //   name={product.name}
    //   price={product.price}
    //   quantity={product.quantity}
    return (

        <div className="carousel-item " data-bs-interval="2000">
            <div className="card">
                <div className="card px-1 py-1">
                    <Link to={`/products/${_id}`}>
                        <img
                        alt={name} src={`/images/${image}`}
                        />
                        <p>{name}</p>
                    </Link>
                    <div>
                        <div>{quantity} {pluralize("item", quantity)} in stock</div> 
                        <span>{price}</span>
                    </div>
                    <button className="addBtn" onClick={addToCart}>Add to cart</button>
                </div>
                <img src={`/images/${image}`} className=" card-img-top" alt="Card image cap" />
                <div className="card-body mb-3 d-md-block">
                    <h3>{name}</h3>
                    <p>Acrylic on canvas, ${price} </p>
                </div>
                <br/>
            </div>
        </div>
    );
}

export default ProductItem;
